mapper.select:
SELECT I_CATALO_ID, V_NOMBRE, V_SEXO, I_CODIGO_POSTAL, D_FECHA_NACIMIENTO
FROM T_CATALOGO_PERSONAS
WHERE I_CATALO_ID = #{id}

mapper.update:
UPDATE T_CATALOGO_PERSONAS
SET V_NOMBRE = #{vNombre},
    V_SEXO = #{vSexo, typeHandler=org.apache.ibatis.type.EnumTypeHandler},
    I_CODIGO_POSTAL = #{iCodigoPostal},
    D_FECHA_NACIMIENTO = #{dFechaNacimiento}
WHERE I_CATALO_ID = #{iPersonaId}
